// this line should not be removed
var wGetNews = angular.module( "wsgNYTimesNews",[]);

wGetNews.controller( "wsgNYTimesNewsController", function( $scope ) {
    var dq = new DataQuery( 'qNyTimes' );
    dq.execute().
    	done( function() {
    		$scope.$apply(function() {
    			$scope.news = dq.getData();
    		});
    	});


	$scope.clickMe = function(selectedIndex) {
	    alert('the content is ' + $scope.news[selectedIndex].label);
	    return '';
	};


	$scope.openArticle = function (selectedIndex) {

	    var newWindow = window.open(
	        $scope.news[selectedIndex].content.url, "_blank");

	   /*$scope.news[selectedIndex].content.url,
	   "_blank",
	   "toolbar=yes, scrollbars=yes, resizable=yes, top=500, left=500, width=400, height=400");*/
	   return '';
	};


});
