// this line should not be removed
var wReadTwitter = angular.module( "wsgTwitter",[]);

wReadTwitter.controller( "wsgTwitterController", function( $scope ) {

    $scope.showFeeds = false;
    //$scope.scrname = 'CIGNA';

    $scope.getTweets = function() { 
    var dq = new DataQuery( 'qTwitter' );
    dq.setParameters({"screen_name":$scope.scrname});
    dq.execute().
        done( function() {
        $scope.$apply(function() {
            $scope.results = dq.getData();
            });
        });

    $scope.showFeeds = true;
    };


    $scope.openWebsite = function (aWebsite) {
	    var newWindow = window.open(
	        aWebsite, "_blank");
	   return '';
	};

	$scope.visitSource = function (selectedIndex) {
	    alert($scope.results[selectedIndex].source);
	    var newWindow = window.open(
	        $scope.results[selectedIndex].source, "_blank");
	   return '';
	};



});
